import React from 'react'
import "./testimonials.scss"

export default function Testimonials() {

  const data = [
    {
      id: 1,
      name: "Nataša Jezdimirović",
      title: "Colleague",
      img:
      "assets/maja.jpeg",
    //  icon: "assets/twitter.png",
      desc:"Always ready to help, great colleague and team member.",
  },
  {
    id: 2,
    name: "Ognjen Srdić",
    title: "Colleague",
    img:
    "assets/ognjen.jpeg",
   // icon: "assets/twitter.png",
    desc: "Nikolaj is great to work with. He is flexible, yet meets all the deadlines.",
    featured: true,

  },
  {
    id: 3,
    name: "Srđan Šuković",
    title: "Colleague",
    img:
    "assets/srdjan.jpeg",
  //  icon: "assets/twitter.png",
    desc: "Excellent friend, he is funny and it is easy to talk with him.",
  },
];
  return (
    <div className='testimonials' id='testimonials'>
        <h1>Testimonials</h1>
        <div className="container">
          {data.map((d)=>(
          <div className={d.featured ? "card featured" : "card"}>
            <div className="top">
              <img src={d.img} className='user' alt="" />
            </div>
            <div className="center">
              {d.desc}
            </div>
            <div className="bottom">
              <h3>{d.name}</h3>
              <h4>{d.title}</h4>
            </div>
          </div>
           ))}
        </div>
    </div>
  )
}
