import React from 'react'
import {useState} from "react";
import axios from 'axios'
import { Component } from 'react';
import "./facilities.scss"
import { useEffect } from 'react';

function Facilities() {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedFacility, setSelectedFacility] = useState(null);
  const [facilities, setFacilities] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/api/facilites/all')
      .then(response => response.json())
      .then(data => setFacilities(data));
  }, []);

  const handleSearchChange = event => {
    setSearchTerm(event.target.value);
  };

  const handleFacilitySelect = facility => {
    console.log('handleFacilitySelect called with', facility);
    setSelectedFacility(facility);
    console.log('selectedFacility set to', selectedFacility);

  };

  const handleFacilityClose = () => {
    setSelectedFacility(null);
  };


   const filteredFacilities =
    facilities && facilities.length > 0
      ? facilities.filter(
          facility =>
            facility &&
            facility.centerName &&
            facility.centerName.toLowerCase().includes(searchTerm.toLowerCase())
        )
      : [];


      /*
  const filteredFacilities = facilities?.filter(facility =>
    facility.centerName.toLowerCase().includes(searchTerm.toLowerCase())
  );
*/

  return (
    <div className="Facilities" id = "facilities">
      <header className="App-header">
        <h1>Facilities</h1>
        <input
          type="text"
          placeholder="Search by name"
          value={searchTerm}
          onChange={handleSearchChange}
        />
      </header>
      <div className="facility-list">
        {filteredFacilities.map(facility => (
          <Facility
            facility={facility}
            onSelect={handleFacilitySelect}
            key={facility.centerId}
          />
        ))}
      </div>
      {selectedFacility !== null && (
        <div className="facility-overlay">
          <div className="facility-detail">
            <h2>{selectedFacility.centerName}</h2>
            <p>{selectedFacility.centerDescription}</p>
            <h3>Number of supplies: {selectedFacility.centerSupplies}</h3>
            <button onClick={handleFacilityClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
}

function Facility({ facility: { centerId, centerName, centerAddress }, onSelect }) {
  return (
    <div className="facility" key={centerId}>
      <h2>{centerName}</h2>
      <p>{centerAddress}</p>
      <button onClick={() => onSelect(centerId)}>See more</button>
    </div>
  );
}

export default Facilities;
