import React from 'react'
import "./profile.scss"

import  { useState } from 'react';
import './profile.scss';

function Profile() {
  const [isEditing, setIsEditing] = useState(false);

  const [profile, setProfile] = useState({
    email: 'john@example.com',
    firstName: 'John',
    lastName: 'Doe',
    address: '123 Main St',
    city: 'New York',
    zipCode: '10001',
    country: 'USA',
    phoneNumber: '123-456-7890',
    jmbg: '123-45-6789',
    gender: 'Male',
    job: 'Software Developer',
    workplace: 'Acme Inc',
    points: 0
  });

  const handleEditClick = () => {
    setIsEditing(true);
  }

  const handleSaveClick = () => {
    setIsEditing(false);
  }

  return (
    <div className="profile-container" id = "profile">
      <div className="leftAndRight">
      <div className="profile-column">
        <div className="profile-field">
          <label>Email:</label>
          {isEditing ? (
            <input type="email" value={profile.email} />
          ) : (
            <p>{profile.email}</p>
          )}
        </div>
        <div className="profile-field">
          <label>First Name:</label>
          {isEditing ? (
            <input type="text" value={profile.firstName} />
          ) : (
            <p>{profile.firstName}</p>
          )}
        </div>
        <div className="profile-field">
          <label>Last Name:</label>
          {isEditing ? (
            <input type="text" value={profile.lastName} />
          ) : (
            <p>{profile.lastName}</p>
          )}
        </div>
        <div className="profile-field">
          <label>Address:</label>
          {isEditing ? (
            <input type="text" value={profile.address} />
          ) : (
            <p>{profile.address}</p>
          )}
        </div>
        <div className="profile-field">
          <label>City:</label>
          {isEditing ? (
            <input type="text" value={profile.city} />
          ) : (
            <p>{profile.city}</p>
          )}
        </div>
        <div className="profile-field">
          <label>Zip Code:</label>
          {isEditing ? (
            <input type="text" value={profile.zipCode} />
          ) : (
            <p>{profile.zipCode}</p>
          )}
        </div>
      </div>
      <div className="profile-column">
        <div className="profile-field">
          <label>Country:</label>
          {isEditing ? (
            <input type="text" value={profile.country} />
          ) : (
            <p>{profile.country}</p>
            )}
          </div>
          <div className="profile-field">
            <label>Phone Number:</label>
            {isEditing ? (
              <input type="text" value={profile.phoneNumber} />
            ) : (
              <p>{profile.phoneNumber}</p>
            )}
          </div>
          <div className="profile-field">
            <label>JMBG:</label>
            {isEditing ? (
              <input type="text" value={profile.jmbg} />
            ) : (
              <p>{profile.jmbg}</p>
            )}
          </div>
          <div className="profile-field">
            <label>Gender:</label>
            {isEditing ? (
              <input type="text" value={profile.gender} />
            ) : (
              <p>{profile.gender}</p>
            )}
          </div>
          <div className="profile-field">
            <label>Job:</label>
            {isEditing ? (
              <input type="text" value={profile.job} />
            ) : (
              <p>{profile.job}</p>
            )}
          </div>
          <div className="profile-field">
            <label>Workplace:</label>
            {isEditing ? (
              <input type="text" value={profile.workplace} />
            ) : (
              <p>{profile.workplace}</p>
            )}
          </div>
          <div className="profile-field">
            <label>Points Collected:</label>
            {isEditing ? (
              <input type="number" value={profile.points} />
            ) : (
              <p>{profile.points}</p>
            )}
          </div>
        </div>
        {isEditing ? (
          <button onClick={handleSaveClick}>Save</button>
        ) : (
          <button onClick={handleEditClick}>Edit</button>
        )}
      </div>

      </div>      
    );
  }

  export default Profile;
