import { List } from '@material-ui/core'
import React from 'react'
import {useEffect,useState} from "react";
import PortfolioList from '../portfolioList/PortfolioList'
import "./portfolio.scss"

export default function Portfolio() {

  const [selected, setSelected] = useState("basicinf")
  const list = [
  {
    id: "basicinf",
    title: "Basic Information"
  },
  {
    id: "social",
    title: "Social"
  }, 
  {
    id: "music",
    title: "Music"
  }
]

  return (
    <div className='portfolio' id='portfolio'>
        <h1>About me</h1>
        <img src='assets/sajt2.jpeg'/>

        <ul>
         {list.map((item) => ( 
           <PortfolioList 
           title = {item.title} 
           active={selected === item.id} 
           setSelected = {setSelected}
           id = {item.id}
           />
         ))}
        </ul>

        <div className="container">

          <div className='bio'>
            <p>My name is Nikolaj Å atara, and currently I am fourth year student of applied computer science and informatics
               at Faculty of Technical Sciences in Novi Sad.</p>

             <p>Born in Prijedor, raised in Zrenjanin, I showed interest in technology ever since first game console came into my hands.
                As time passed interest in mostly hardware, through internet and forums, outgrown to shared software, linux and programming. 
                At that time I was still in highschool, so I used the opportunity to visit Petnica seminars, to connect with other people with similar hobbys, and learn more.</p> 

             <p>Petnica was big part of my highschool life, I've spent three years on different seminars, all having one in common: Technology.</p>
             <p>Seminars that I attended were: Technology and robotics, applied physics and electronics, and information technologies seminar.
               At that time I was active in Linux user groups of Novi Sad and Zrenjanin (LUGoNS and LUGoZr), and volunteer on BALCCON (Balcan Compunting Congress). </p>
              <p>After highschool I decided to take studies in nearby Novi Sad. Nothing much has changed since the elementary school, on colleague I still love same things, and hope to be big part of IT community.</p>
              <p>As for non-professional interests, I play guitar and love basketball. Teams that I support are KK Crvena Zvezda and Denver Nuggets.</p>

          </div>
        </div> 
    </div>
  )
}
