import React, {useState,useEffect, useContext} from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import {useNavigate} from 'react-router-dom';
import  AuthContext  from './../context/authProvider';

import "./login.scss"



const LoginPage = () => {
  /*
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
    //   navigate('#profile');
    }
  }, [navigate])

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const res = await axios.post('http://localhost:8080/api/auth/signin', {
        email: email,
        password: password
      });
      console.log(res);
      // handle response
      if (res.status === 200) {
        // Save token in local storage
        localStorage.setItem('token', res.data.token);
        //navigate('#profile');
        // Redirect to the profile page
    }
    } catch (error) {
      console.log(error);
      // handle error
    }
  }
*/
const navigate = useNavigate();
const location = useLocation();
const from = location.state?.from?.pathname || "/home";
const { auth, setAuth } = useContext(AuthContext);
//state the name of the employee gotten from the form
const [email, setEmail] = useState("");
//state to store password of the employee gotten from the form
const [password, setPassword] = useState("");

const handleSubmit = async (e) => {
  e.preventDefault();
  try {
    fetch(`http://localhost:8080/api/auth/signin`, {
      method: "POST",
      headers: {"content-Type": "application/json"},
      body: JSON.stringify({
        email: email,
        password: password,
      }),
    })
      .then((res) => res.json())
      .then((res) => {
        if (res.name) {
          console.log(res);
          const token = res.token;

            //storing employee information in our Auth state
          setAuth({ token, email });
          console.log(auth);
          setEmail("");
          setPassword("");
          //navigate our employee to the home page
          navigate(from, { replace: true });
        } else {
          console.log("incorrect submission");
        }
      });
  } catch (err) {
    if (!err?.response) {
      console.log("no server response");
    } else {
      console.log("registeration failed");
    }
  }
};
  return (
    <div className="login-container" id = "login">
      <div className="card">
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <h1 className = "h11">Login</h1>
            <div className="form-group">
              <label htmlFor="email">Email:</label>
              <input 
                type="email" 
                className="form-control" 
                id="username" 
                value={email} 
                onChange={e => setEmail(e.target.value)} 
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password:</label>
              <input 
                type="password" 
                className="form-control" 
                id="password" 
                value={password} 
                onChange={e => setPassword(e.target.value)} 
              />
            </div>
            <button type="submit" className="btn btn-primary">
              Submit
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
