import React from 'react'
import {useState} from "react";
import axios from 'axios'
import { Component } from 'vue';
import "./facilities.scss"

export default class Facilities extends Component {

  constructor(props){
    super(props)

    this.state = {
      facilityList : []
    }
  }

  componentDidMount(){
    axios.get('http://localhost:8080/api/facilities/all')
    .then(response => {
        this.setState({facilityList: response.data})
    })
    .catch(error => {
        console.log(error)
    })
}
render() {
    const {facilityList} = this.state
    return (
        <div>
            {
                facilityList.length ?
                facilityList.map(post => <div key={post.id}>{post.title}</div>)
                :
                null
            }
        </div>
    )
}
}
